CREATE TABLE hotel (
  id BIGSERIAL PRIMARY KEY NOT NULL,
  name VARCHAR (255) NOT NULL,
  city VARCHAR (255) NOT NULL,
  country VARCHAR (255) NOT NULL,
  rating INT NOT NULL,
  description TEXT NOT NULL
);

